const axios = require('axios')

module.exports = async function(animal) {
  let animals = ['cat', 'dog']
  if (!animal || !animals.includes(animal)) return console.error('Pets.js Error: Invalid pet provided, please provide "cat" or "dog." You provided: ' + animal)
  try {
    let data = await axios({
      method: 'get',
      url: `https://api.the${animal}api.com/v1/images/search`
    })
    let url = data.data[0].url
    return url
  } catch (e) {
    console.error('Pets.js Error: '+e)
  }
}
